      <div>
      {flashcard.front}
      {flashcard.one_back}
      <input onChange = {(e) => inputValueChanged(e)} type="text" value = {input} autoFocus/>
      </div>
    )

  }
  else if (count === 1) {
    return (
      <div>
        <p>{flashcard.front }</p>
        <p>{flashcard.one_back}</p>
        {/* <button onClick={() => textSynthesis(flashcard.front)}>Again</button> */}
