export default function FlashcardSet({ selectedSet }) {
  const GameComponent = ({ flashcard }) => {
    const [input, setInput] = useState("");
    const [count, setCount] = useState(0);

    function inputValueChanged(e, flashcard) {
      if (input === flashcard) {
        return setCount(count + 1);
      }
      console.log();
      setInput(e.target.value);
    }

    function textSynthesis(flashcardfront) {
      const synth = window.speechSynthesis;
      const utterance = new SpeechSynthesisUtterance(flashcardfront);
      synth.speak(utterance);
      console.log(utterance);
    }

    if (count === 0) {
      return (
        <div>
          {flashcard.front}
          {flashcard.one_back}
          <input
            onChange={(e) => inputValueChanged(e, flashcard.front)}
            type="text"
            value={input}
          />
        </div>
      );
    } else if (count === 1) {
      textSynthesis(flashcard.front);
      return (
        <div>
          {flashcard.front}
          {flashcard.one_back}
          <button onClick={() => textSynthesis(flashcard.front)}>
            Again
          </button>
        </div>
      );
    }

    return null;
  };

  return (
    <div>
      {Array.isArray(selectedSet) &&
        selectedSet.map((high) =>
          high.map((flashCard) => (
            <div key={flashCard.flashcard.id}>
              <GameComponent flashcard={flashCard.flashcard} />
            </div>
          ))
        )}
    </div>
  );
}
