import {useState} from 'react'

function FlashcardSet({ selectedSet }) {
  const GameComponent = (flashcard) => {
  // map through each card
  // function in map taking in card

  // function will be called on each card
const [input, setInput] = useState("")
const [count, setCount] = useState(0)

function inputValueChanged(e, flashcard){
  if (input === flashcard) {
    return setCount(count + 1)
  }
  console.log()
  setInput(e.target.value)
}

function textSynthesis(flashcardfront){
  const synth = window.speechSynthesis;
  const utterance = new SpeechSynthesisUtterance(flashcardfront);
  synth.speak(utterance);
  console.log(utterance)

}

    if (count === 0){
// i want to have the card displayed and it must be typed correctly to move on
// with accent keyboard below the game
    return (
      <div>
      {flashcard.front}
      {flashcard.oneback}
      <input onChange = {(e) => inputValueChanged(e, flashcard.front)} type="text" value = {input}/>
      </div>
    )

  }
  else if (count = 1) {
    // I want to have the card front displayed and it will be spoken to the user
    textSynthesis(flashcard.front)

    return (
      <div>
        {flashcard.front}
        {flashcard.oneback}
        <button onClick = {textSynthesis(flashcard.front)}>Again</button>
      </div>
    )
    }
    // hear it repeated then move on

    //^ to be in useEffect
// if clicked reset the timer

  }
  // else if (count = 2) {
  //   // Speech recognition of flashcard.front

  // }

  return (
    <div>
      {Array.isArray(selectedSet) && selectedSet.map((flashCard) => (
        <div key={flashCard.id}>
          {GameComponent(flashCard)}
          {/* Add more elements to render additional data */}
        </div>
      ))}
    </div>
  );

}

export default FlashcardSet;
