import { useState, useEffect } from 'react'

function Flashcard({ languageApp, flashGet }) {
  const [flashCards, setFlashCards] = useState([]);

  useEffect(() => {
    fetch(`/flashcards/${flashGet.id}`)
      .then((r) => {
        if (r.ok) {
          r.json().then((cards) => setFlashCards(cards));
        } else {
          setFlashCards([]);
        }
      });
  }, []);

  const handleSetClick = (setIndex) => {
    const startIndex = setIndex * 10;
    const endIndex = startIndex + 9;
    const setOfCards = flashCards.slice(startIndex, endIndex + 1);
    // Pass the set of cards and the index to the child component
    // Only the sets after the one clicked will be passed down
    return <ChildComponent key={setIndex} setIndex={setIndex} setOfCards={setOfCards} />;
  };

  return (
    <div>
      {[...Array(Math.ceil(flashCards.length / 10)).keys()].map((setIndex) => (
        <button key={setIndex} onClick={() => handleSetClick(setIndex)}>
          {`${setIndex * 10 + 1} - ${(setIndex + 1) * 10}`}
        </button>
      ))}
    </div>
  );
}

function ChildComponent({ setIndex, setOfCards }) {
  return (
    <div>
      <h3>Set {setIndex + 1}</h3>
      {setOfCards.map((card) => (
        <div key={card.id}>
          <h2>{card.title}</h2>
          <p>{card.flashcard.front}</p>
          <p>{card.flashcard.one_back}</p>
          {/* Add more elements to render additional data */}
        </div>
      ))}
    </div>
  );
}

export default Flashcard;
