import {useState, useEffect} from 'react'

function Flashcard({userLanguages}){
const [flashCards, setFlashCards] = useState([])

useEffect(() => {
  // fetch the join table and display the titles
    fetch(`/flashcaards/${userLanguages.id}`)
    .then((r) => {
      if (r.ok) {
        r.json().then((cards) => setFlashCards(cards));
      } else {
        setFlashCards(null)
      }
    });
  },[]);
return (
  <div>
    hello
    {console.log(flashCards)}

  </div>
)

}
