import React, { useState } from 'react';

function SignupForm({languageApp}) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [password_confirmation, setPasswordConfirmation] = useState('');
  const [email, setEmail] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch('/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password, password_confirmation, email }),
      });
      const data = await response.json();
      console.log(data);
      // handle successful signup
    } catch (error) {
      console.error(error);
      // handle error
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
      {translation[languageApp][0]}
        <input type="username" value={username} onChange={(e) => setUsername(e.target.value)} />
      </label>
      <br />
      <label>
      {translation[languageApp][1]}
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
      </label>
      <br />
      <label>
      {translation[languageApp][2]}
        <input type="email" value={password} onChange={(e) => setPasswordConfirmation(e.target.value)} />
      </label>
      <br />
      <label>
      {translation[languageApp][3]}
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
      </label>
      <br />
      <button type="submit">{translation[languageApp][4]}</button>
    </form>
  );
}

export default SignupForm;

const translation = {
  "en-US": ["Username:", "Password:", "Password Confirmation:", "Email:", "Sign up"],
