    return (
      <div>
      {flashcard.front}
      {flashcard.one_back}
      <input onChange = {(e) => inputValueChanged(e)} type="text" value = {input} autoFocus/>
      </div>
    )

  }
  else if (count === 1) {
    // textSynthesis()

    // if speaking then do nothing
    // if parameter is passed then run only the last timer that does not repeat
