  //     if (timerStart === 3) {
  //      textSynthesis()
  //     }
  //     else if(timerStart === 0){
  //      textSynthesis()
  //     }
  //     if(timerStart === 0){
  //       clearInterval(interval)
  //     }
  //     }, 1000);
    return (
      <div>
        <p>{flashcard.front }</p>
        <p>{flashcard.one_back}</p>
        {/* <button onClick={() => textSynthesis(flashcard.front)}>Again</button> */}
      </div>
    );
  }
  else if (count == 2) {

      return (
        <div>
          <p>Repeat the front of the card:</p>
          <p>{flashcard.front}</p>
        </div>
      );
    }

    // hear it repeated then move on

    //^ to be in useEffect
// if clicked reset the timer

  }
  // else if (count = 2) {
  //   // Speech recognition of flashcard.front

  // }

  return (
    <div>

  <div key={index}>
{ <GameComponent flashcard = {selectedSet[index].flashcard }/>}

  </div>

    </div>
  );

}

export default FlashcardSet;
