import React from 'react';
import {useState} from 'react'

function FlashcardSet({ selectedSet }) {
  // map through each card
  // function in map taking in card

  // function will be called on each card
const [input, setInput] = useState("")
const [count, setCount] = useState(0)
  function GameComponent(flashcard) {
    setCount(0)
    if (count = 0){
// i want to have the card displayed and it must be typed correctly to move on
// with accent keyboard below the game
      function inputValueChanged(e){
        setInput(e.target.value)
        if (input === flashcard.front) {
          return count + 1
        }
      }
    return (
      <div>
      {flashcard.front}
      {flashcard.oneback}
      <input onChange = {(e) => inputValueChanged(e)} type="text" value = {input}/>
      </div>
    )

  }
  else if (count = 1) {
    // I want to have the card front displayed and it will be spoken to the user
    // hear it repeated then move on
    function textSynthesis(){
      const synth = window.speechSynthesis;
      const utterance = new SpeechSynthesisUtterance(flashcard.front);
      synth.speak(utterance);

    }
textSynthesis()

    //^ to be in useEffect
// if clicked reset the timer

    return (
      <div>
        {flashcard.front}
        {flashcard.oneback}
        <button onClick = {textSynthesis}>Again</button>
      </div>
    )
  }
  else if (count = 2) {
    // Speech recognition of flashcard.front
  }
}
  return (
    <div>
      {selectedSet.map((flashCard) => (
        <div key={flashCard.id}>
          <h2>{flashCard.title}</h2>
          <p>{flashCard.flashcard.front}</p>
          <p>{flashCard.flashcard.one_back}</p>
          {/* Add more elements to render additional data */}
        </div>
      ))}
    </div>
  );
}

export default FlashcardSet;
