      setTimerEnd(timerEnd + 1)
    }
    else if (timerStart === 2) {
      textSynthesis();
    }
    else if (timerStart === 7) {
      textSynthesis();
    } 
  }, 1000);
}



  const GameComponent = ({flashcard}) => {
  // map through each card
