import {useState} from 'react'

function FlashcardSet({ selectedSet }) {

  const [input, setInput] = useState("")
  const [count, setCount] = useState(0)
  const [index, setIndex] = useState(0)
  const [timerStart, setTimerStart] = useState(0)
  const [timerMiddle, setTimerMiddle] = useState(0)
  const [timerEnd, setTimerEnd] = useState(0)
  // const [timer3, setTimer3] = useState([])
  // function will be called on each card
// input value will set count +1 if correct


  function inputValueChanged(e) {
    e.preventDefault()
    if (e.target.value === selectedSet[index].flashcard.front) {
      setCount(count + 1);
      setInput('');
    } else {
      setInput(e.target.value);
    }
  }
// text synthesis function
function textSynthesis(){
  const synth = window.speechSynthesis;
  const utterance = new SpeechSynthesisUtterance(selectedSet[index].flashcard.front);
  utterance.lang = "de-DE"
  synth.speak(utterance);
  // console.log(utterance)

}
