import './App.css';
import Login from './Components/Login';
import NavBar from './Components/Navbar';
import Signup from './Components/Signup';
// import Home from './Components/Home';
import Footer from './Components/Footer';
import LanguagePicker from './Components/LanguagePicker';
import Course from './Components/Course';
import AboutUs from './Components/AboutUs';
import Flashcard from './Components/Flashcard';
import {
  createBrowserRouter,
  RouterProvider,
} from "react-router-dom";
import {useState, useEffect} from 'react';


function App() {
  const [languageApp, setLanguageApp] = useState("en-US")
  const [user, setUser] = useState(null)


  // setting 
  // const [language]
  // fetch user then set a language state to the primary language of the app

  // useEffect(() => {
  //   // Set the language state to the user's browser language
  // }, []);
  // let userLang = navigator.language || navigator.userLanguage;
  // user ? (setLanguageApp(user.language) : setLanguageApp(userLang))
  useEffect(() => {
    let userLang = navigator.language || navigator.userLanguage;
    if (user && user.language) {
      setLanguageApp(user.language);
    } else {
      setLanguageApp(userLang);
    }
  }, [user]);


const [userLanguages, setUserLanguages] = useState([])

// useEffect(() => {

//     fetch('http://localhost:3000/languages')
//       .then((res) => {
//         console.log(res.json)
//         if (res.ok) {
//           res.json().then((data) => setUserLanguages(data))
//         } 
//         else {
//           setUserLanguages(null);
//         }
//       });
//   }, []);

  useEffect(() => {
    fetch("/login")
    .then((r) => {
      if (r.ok) {
        r.json().then((user) => setUser(user));
      } else {
        setUser(null)
      }
    });
  },[]);
  // useEffect(() => {
  //   fetch("/show")
  //   .then((r) => {
  //     if (r.ok) {
  //       r.json().then((user) => setUser(user));
  //     } else {
  //       setUser(null)
  //     }
  //   });
  // },[]);


  const router = createBrowserRouter([

    {
      path:"/*",
      element: <h1>404 not found</h1>
    },
    {
      path:"/login",
      element: <Login />
    },
    {
      path: "/",
      // element: <Home user={user}/>
      element: <Course languageApp = {languageApp} userLanguages = {userLanguages} setUserLanguages={setUserLanguages}/>
    },
    // {
    //   path: "/AddLanguages",
    //   element: <Languages />
    // },
      {
        path: "/languages",
        element: <LanguagePicker userLanguages = {userLanguages}  setUserLanguages = {setUserLanguages}/>
      },
      {
        path: "/courses",
        element: <Course languageApp = {languageApp} userLanguages = {userLanguages} setUserLanguages={setUserLanguages}/>
      },
      {
        path: "/flashcards",
        element: <Flashcard userLanguages = {userLanguages}/>
      }
  ]);

  //gets current language
  //return whatever the route is on
  return (
    <div className="App">
      <NavBar user={user}/>
      {user ? (
        <div className = "about-us">
        <AboutUs />
        <Signup />
        </div>
       ):(
         <RouterProvider router={router} />

      )} 
      <div>

<Footer languageApp={languageApp}/>
        </div>
    </div>
  );
}

export default App;
