  transcript,
  resetTranscript,
  listening,
  browserSupportsSpeechRecognition
} = useSpeechRecognition({
  // Pass in the language configuration object
  lang: 'en-US',
  continous: true
});
if (!browserSupportsSpeechRecognition) {
  return (
    <div>
