import {useState, useEffect} from 'react'

function Flashcard({languageApp, flashGet}){
const [flashCards, setFlashCards] = useState([])

useEffect(() => {
  // fetch the titles of the flashcards by number
    fetch(`/flashcards/${flashGet.id}`)
    .then((r) => {
      if (r.ok) {
        r.json().then((cards) => setFlashCards(cards));
      } else {
        setFlashCards(null)
      }
    });
  },[]);
return (
  <div>
    {flashCards.map((flashCard) => {
      return (
        <div> 
          {flashCard.front}
          {flashCard.back[languageApp]}
        </div>
      )
    })}
  </div>
)

}
export default Flashcard
