import { useState, useEffect } from 'react'

function Flashcard({ languageApp, flashGet }) {
  const [flashCards, setFlashCards] = useState([])
  const [cardSets, setCardSets] = useState([])
  const [selectedSet, setSelectedSet] = useState(0)

  useEffect(() => {
    fetch(`/flashcards/${flashGet.id}`)
      .then((r) => {
        if (r.ok) {
          r.json().then((cards) => {
            setFlashCards(cards)
            setCardSets(chunkArray(cards, 10))
          });
        } else {
          setFlashCards([])
        }
      });
  }, []);

  function chunkArray(array, size) {
    const chunkedArray = []
    for (let i = 0; i < array.length; i += size) {
      const chunk = array.slice(i, i + size)
      chunkedArray.push(chunk)
    }
    return chunkedArray
  }

  function handleSetClick(setIndex) {
    setSelectedSet(setIndex)
  }

  return (
    <div>
      {cardSets.map((cardSet, index) => (
        <button key={index} onClick={() => handleSetClick(index)}>
          {`${index * 10 + 1}-${(index + 1) * 10}`}
        </button>
      ))}
      <div>
        {cardSets[selectedSet].map((flashCard) => {
          return (
            <div key={flashCard.id}>
              <h2>{flashCard.title}</h2>
              <p>{flashCard.flashcard.front}</p>
              <p>{flashCard.flashcard.one_back}</p>
            </div>
          )
        })}
      </div>
    </div>
  )
}

export default Flashcard
