import {useState, useEffect} from 'react'

function Flashcard({languageApp, flashGet}){
  const [currentCardSetIndex, setCurrentCardSetIndex] = useState(0);
  const [cardSets, setCardSets] = useState([]);
  const [flashCards, setFlashCards] = useState([])


  useEffect(() => {
    // fetch the titles of the flashcards by number
    fetch(`/flashcards/${flashGet.id}`)
      .then((r) => {
        if (r.ok) {
          r.json().then((cards) => {
            setFlashCards(cards);
            const newCardSets = [];
            for (let i = 0; i < cards.length; i += 10) {
              const cardSet = cards.slice(i, i + 10);
              newCardSets.push(cardSet);
            }
            setCardSets(newCardSets);
          });
        } else {
          setFlashCards([]);
        }
      });
  }, []);



  return (
    <div>
      {cardSets[currentCardSetIndex].map((flashCard) => {
        return (
          <div key={flashCard.id}>
            <h2>{flashCard.title}</h2>
            <p>{flashCard.flashcard.front}</p>
            <p>{flashCard.flashcard.one_back}</p>
            {/* Add more elements to render additional data */}
          </div>
        );
      })}
      {cardSets.map((cardSet, index) => (
        <button key={index} onClick={() => setCurrentCardSetIndex(index)}>
          {index * 10 + 1}-{index * 10 + cardSet.length}
        </button>
      ))}
    </div>
  );



}
export default Flashcard
