import { useState, useEffect } from 'react';

function FlashcardSet({ selectedSet, languageApp }) {
  const [input, setInput] = useState("");
  const [count, setCount] = useState(0);


  useEffect(() => {
    setInput("");
    setCount(0);
  }, [flashcard]);

  function inputValueChanged(e, flashcard) {
    if (input === flashcard.answer) {
      setCount(count + 1);
    }
    setInput(e.target.value);
  }

  function textSynthesis(flashcardfront) {
    const synth = window.speechSynthesis;
    const utterance = new SpeechSynthesisUtterance(flashcardfront);
    utterance.lang = languageApp;
    synth.speak(utterance);
  }

  function conditional(flashcard) {
    if (count === 0) {
      return (
        <div>
          {flashcard.front}
          {flashcard.oneback}
          <textarea onChange={(e) => inputValueChanged(e, flashcard)} value={input}></textarea>
        </div>
      );
    }
    else if (count === 1) {
      textSynthesis(flashcard.front);

      return (
        <div>
          {flashcard.front}
          {flashcard.oneback}
          <button onClick={() => textSynthesis(flashcard.front)}>Again</button>
        </div>
      );
    }
    else {
      return <div>Game Over!</div>;
    }
  }

  return (
    <div>
      {selectedSet.map((flashcard) => (
        <div key={flashcard.id}>
          {conditional(flashcard)}
        </div>
      ))}
    </div>
  );
}

export default FlashcardSet;
